// Generate TwiML Voice Response
// Documentation: https://www.twilio.com/docs/voice/twiml

const caller = $input.first().json.body?.From || 'Unknown';
const calledNumber = $input.first().json.body?.To || 'Unknown';
const callSid = $input.first().json.body?.CallSid || 'Unknown';
const callerCity = $input.first().json.body?.CallerCity || '';
const callerCountry = $input.first().json.body?.CallerCountry || '';

// Format caller number for voice (add pauses between digits)
const formatPhoneForVoice = (phone) => {
  return phone.replace(/[^0-9+]/g, '').split('').join(' ');
};

// Create TwiML response with text-to-speech
const twimlResponse = `<?xml version="1.0" encoding="UTF-8"?>
<Response>
  <Say voice="alice" language="es-ES">
    Hola! Gracias por llamar. Este es un mensaje de prueba de integracion con Twilio.
  </Say>
  <Pause length="1"/>
  <Say voice="alice" language="es-ES">
    Has llamado desde el numero ${formatPhoneForVoice(caller)}.
  </Say>
  <Pause length="1"/>
  <Say voice="alice" language="es-ES">
    Si deseas mas informacion, por favor visite nuestra pagina web.
    Hasta pronto!
  </Say>
</Response>`;

return [{
  json: {
    twiml: twimlResponse,
    callData: {
      callSid: callSid,
      caller: caller,
      calledNumber: calledNumber,
      callerCity: callerCity,
      callerCountry: callerCountry
    },
    timestamp: new Date().toISOString()
  }
}];
